package oop.h4t.tetris;/** * Group 15: H4T * On 30/11/15. */// TPoint.java/* Cung cap toa do x, y cua tung hinh*/public class TPoint implements Comparable<TPoint> {    public int x;    public int y;    public int compareTo(TPoint other) {        if (other.x < x)            return -1;        if (other.x > x)            return 1;        if (other.y < y)            return -1;        if (other.y > y)            return 1;        return 0;    }    public TPoint(int x, int y) {        this.x = x;        this.y = y;    }    public TPoint(TPoint point) {        this.x = point.x;        this.y = point.y;    }    public boolean equals(Object other) {        if (this == other) return true;        if (!(other instanceof TPoint)) return false;        TPoint pt = (TPoint) other;        return (x == pt.x && y == pt.y);    }    public String toString() {        return "(" + x + "," + y + ")";    }}